import React, { useState, useEffect } from "react";
import "../styles/Prest_.css";
import { Link, useHistory } from "react-router-dom";

import { ButtonLarge } from "../components/utilities/Buttons";
import Layout from "../components/utilities/Layout";
import Title from "../components/utilities/Title";
import { useAuth } from "../context/userContext"; // context
import { ButtonSmall } from "../components/utilities/Buttons";
import Header from "../components/Header";

const Prest_Step0 = () => {
  const [windowsWidth, setWindowsWidth] = useState(window.innerWidth);

  const history = useHistory();
  const { setCurrentStepProcess } = useAuth(); // context

  function isMobile() {
    if (windowsWidth > 1100) {
      return false;
    } else {
      return true;
    }
  }

  function handleResize(width) {
    setWindowsWidth(width);
  }

  useEffect(() => {
    window.addEventListener("resize", (e) => handleResize(window.innerWidth));
  }, []);

  setCurrentStepProcess(0);

  return (
    <>
      <Header />
      <Layout>
        <div className="membership">
          <div className="membership_header">
            <h1>
              Dernière étape avant de rentrer<br /> officiellement dans la
              <span> famille!</span>
            </h1>
            <div className="membership_header_images">
              <div className="images_container">
                <div className="item"></div>
                <div className="item"></div>
                <div className="item"></div>
                <div className="item"></div>
                <div className="item"></div>
              </div>
            </div>
          </div>
          <div className="membership_content">
            <div className="item">
              <span>2020</span>
              <p>
                Notre aventure a commencée en <span>Avril 2020</span>, la
                création de <span>Nivid</span> était évidente...
              </p>
              <p>
                En <span>juin 2020</span>, la création du site est à ses débuts,
                l'équipe s'aggrandit !
              </p>
            </div>
            <div className="item">
              <span>2021</span>
              <p>
                Nous rencontrons nos premiers commerces locaux pour construire{" "}
                <span>Nivid</span> ensemble.
              </p>
              <p>
                En <span>octobre</span>, deux nouvelles personnes intègrent
                l'équipe pour nous aider dans la communication.
              </p>
            </div>
          </div>
          <div className="membership_bottom">
            <h2>Continuons d'écrire l'histoire ensemble...</h2>
            <ButtonSmall
              onClick={() => history.push("/inscription")}
              color="blue"
              value="Je commence mon évènement maintenant"
            />
          </div>
        </div>
      </Layout>
    </>
  );
};
export default Prest_Step0;
