import React, { useState, useEffect } from "react";
import "../styles/Prest_.css";
import { Link, useHistory } from "react-router-dom";

import { ButtonLarge } from "../components/utilities/Buttons";
import Layout from "../components/utilities/Layout";
import Title from "../components/utilities/Title";
import { useAuth } from "../context/userContext"; // context
import { ButtonSmall } from "../components/utilities/Buttons";
import Header from "../components/Header";

const Prest_Step0 = () => {
  const [windowsWidth, setWindowsWidth] = useState(window.innerWidth);

  const history = useHistory();
  const { setCurrentStepProcess } = useAuth(); // context

  function isMobile() {
    if (windowsWidth > 1100) {
      return false;
    } else {
      return true;
    }
  }

  function handleResize(width) {
    setWindowsWidth(width);
  }

  useEffect(() => {
    window.addEventListener("resize", (e) => handleResize(window.innerWidth));
  }, []);

  setCurrentStepProcess(0);

  return (
    <>
      <Header />
      <Layout>
        <div className="membership">
          <div className="membership_header">
            <h1>
              Dernière étape avant de rentrer
              <br /> officiellement dans la
              <span> famille!</span>
            </h1>
            <div className="membership_header_images">
              <div className="images_container">
                <div className="item"></div>
                <div className="item"></div>
                <div className="item"></div>
                <div className="item"></div>
                <div className="item"></div>
              </div>
            </div>
          </div>
          <div className="membership_content">
            <p>
              En rejoignant <span>Nivid</span>, vous intégrez une communauté de
              professionnels passionés et résolument modernes. Un petit luxe
              dont on ne sait plus se passer une fois que l'on y a goûté !
            </p>
            <div className="features">
                <div>
                     {/* icon */}
                    <h4>Augmentez votre chiffre d'affaire</h4>
                    <p>Grâce à une plateforme intuitive & personnalisée</p>
                </div>
                <div>
                     {/* icon */}
                    <h4>Gagnez en productivité</h4>
                    <p>Fini les multiples plateformes, faites tout depuis un seul endroit</p>
                </div>
            </div>
            <div className="offer">
                <h1>1 seule offre complète</h1>
                <span>468€/an</span>
                <p>Tout compris, pas de frais cachés ou de frais supplémentaires.</p>
                <ul>
                    <li>Accès en illimité à la plateforme Nivid & ses outils</li>
                    <li>Mise en relation avec des milliers de particuliers lors de la création de leurs évènements</li>
                    <li>Outils d'analyse de performances ( vues, likes de votre profil, nombre de vente )</li>
                    <li>Facturation automatique & système de gestion des commandes ( c'est vous le chef ! )</li>
                </ul>
            </div>
          </div>
          <div className="membership_bottom">
            <h2>Continuons d'écrire l'histoire ensemble...</h2>
            <ButtonSmall
              onClick={() => history.push("/inscription")}
              color="blue"
              value="Je commence mon évènement maintenant"
            />
          </div>
        </div>
      </Layout>
    </>
  );
};
export default Prest_Step0;
